package com.rendonsoft.beerappdemotest.commons.networkimport com.jakewharton.retrofit2.adapter.kotlin.coroutines.CoroutineCallAdapterFactoryimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryclass Network {    private companion object {        const val URL_BASE = "https://api.punkapi.com/v2/"    }    private val retrofit = Retrofit            .Builder()            .baseUrl(URL_BASE)            .addConverterFactory(GsonConverterFactory.create())            .addCallAdapterFactory(CoroutineCallAdapterFactory())            .build()    fun getInstance() = retrofit}