package com.rendonsoft.beerappdemotest.feature.detail.framework.implementation.data.dataSourceimport com.rendonsoft.beerappdemotest.commons.database.dao.BeerDaoimport com.rendonsoft.beerappdemotest.feature.detail.data.dataSource.DetailBeerDataSourceimport com.rendonsoft.beerappdemotest.feature.detail.domain.BeerDetailModelimport com.rendonsoft.beerappdemotest.feature.detail.framework.implementation.data.dataSource.mapper.BeerDtoToBeerDetailModelMapperclass DetailBeerLocalDataSourceImpl(        private val beerDao: BeerDao,        private val beerDtoToBeerDetailModelMapper: BeerDtoToBeerDetailModelMapper,) : DetailBeerDataSource {    override suspend fun getBeerById(beerId: Int): BeerDetailModel {        return beerDtoToBeerDetailModelMapper.map(beerDao.getBeersById(beerId))    }}