package com.rendonsoft.beerappdemotest.feature.detail.usesCaseimport com.rendonsoft.beerappdemotest.feature.detail.data.DetailBeerRepositoryimport com.rendonsoft.beerappdemotest.feature.detail.domain.BeerDetailModelclass GetBeerByIdUsesCase(        private val detailBeerRepository: DetailBeerRepository) {    suspend operator fun invoke(beerId: Int): BeerDetailModel {        return detailBeerRepository.getBeerById(beerId)    }}