package com.rendonsoft.beerappdemotest.feature.home.framework.implementation.data.dataSource.localimport com.rendonsoft.beerappdemotest.commons.database.dao.BeerDaoimport com.rendonsoft.beerappdemotest.commons.database.models.BeerDtoimport com.rendonsoft.beerappdemotest.feature.home.data.dataSource.HomeBeerLocalDataSourceimport com.rendonsoft.beerappdemotest.feature.home.domain.model.BeerModelimport com.rendonsoft.beerappdemotest.feature.home.framework.implementation.data.dataSource.local.mapper.BeerDtoToBeerModelMapperclass HomeBeerLocalDataSourceImpl(        private val beerDao: BeerDao,        private val beerDtoToBeerModelMapper: BeerDtoToBeerModelMapper,) : HomeBeerLocalDataSource {    override suspend fun getBeersByPage(page: Int): List<BeerModel> {        return beerDao.getBeersByPage(page).map { beerDtoToBeerModelMapper.map(it) }    }    override suspend fun saveBeers(vararg beers: BeerDto): Unit {        beerDao.insertBeer(beers = beers)    }}