package com.rendonsoft.beerappdemotest.feature.home.framework.implementation.data.dataSource.remoteimport com.rendonsoft.beerappdemotest.commons.network.Networkimport com.rendonsoft.beerappdemotest.feature.home.data.dataSource.HomeBeerRemoteDataSourceimport com.rendonsoft.beerappdemotest.feature.home.framework.implementation.data.api.BeerApiimport com.rendonsoft.beerappdemotest.feature.home.framework.implementation.data.config.response.Beerclass HomeBeerRemoteDataSourceImpl(        private val network: Network) : HomeBeerRemoteDataSource {    override suspend fun getBeerByPage(page: Int, pageSize: Int): List<Beer> {        val response = network.getInstance().create(BeerApi::class.java).getBeersAsync(page, pageSize).await()        return try {            if (response.isSuccessful) {                response.body()?.map { it.copy(page = page) } ?: listOf()            } else {                listOf()            }        } catch (ex: Exception) {            ex.printStackTrace()            listOf()        }    }}