package com.rendonsoft.beerappdemotest.feature.home.framework.presentation.page.widgetsimport androidx.compose.foundation.layout.Arrangementimport androidx.compose.foundation.layout.Columnimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.layout.sizeimport androidx.compose.foundation.layout.widthimport androidx.compose.material3.Cardimport androidx.compose.material3.CardDefaultsimport androidx.compose.material3.ExperimentalMaterial3Apiimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dpimport coil.compose.AsyncImageimport com.rendonsoft.beerappdemotest.Rimport com.rendonsoft.beerappdemotest.feature.home.domain.model.BeerModel@OptIn(ExperimentalMaterial3Api::class)@Composablefun BeerCard(        data: BeerModel,        onClick: (Int) -> Unit,        modifier: Modifier = Modifier,) {    Card(            shape = MaterialTheme.shapes.small,            elevation = CardDefaults.cardElevation(8.dp),            onClick = {                onClick.invoke(data.id)            },            modifier = modifier.padding(horizontal = 16.dp),            colors = CardDefaults.cardColors(containerColor = MaterialTheme.colorScheme.background)    ) {        Row(modifier = Modifier                .fillMaxWidth()                .padding(bottom = 8.dp, top = 8.dp)) {            Spacer(modifier = Modifier.width(16.dp))            AsyncImage(                    model = data.imageUrl,                    placeholder = painterResource(R.drawable.icon_launch),                    error = painterResource(R.drawable.icon_launch),                    contentDescription = null,                    modifier = Modifier.size(60.dp)            )            Column(verticalArrangement = Arrangement.spacedBy(4.dp),                    modifier = Modifier.padding(start = 16.dp, end = 16.dp)) {                Text(text = data.name, style = MaterialTheme.typography.labelMedium)                Text(text = data.tagLine, style = MaterialTheme.typography.labelSmall)                Text(text = data.contributedBy, style = MaterialTheme.typography.labelSmall)            }        }    }}@Preview@Composablefun CardBeerPreview() {    BeerCard(            data = BeerModel(                    id = 0,                    name = "Buzz",                    tagLine = "A Real Bitter Experience.",                    firstBrewed = "09/2007",                    imageUrl = "https://images.punkapi.com/v2/keg.png",                    contributedBy = "Sam Mason <samjbmason>",            ),            onClick = {            },            modifier = Modifier.fillMaxWidth()    )}