package com.rendonsoft.beerappdemotest.feature.home.framework.presentation.viewModelimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport androidx.paging.PagingDataimport androidx.paging.cachedInimport com.rendonsoft.beerappdemotest.feature.home.domain.model.BeerModelimport com.rendonsoft.beerappdemotest.feature.home.useCases.GetBeersByPageUsesCaseimport kotlinx.coroutines.flow.MutableStateFlowimport kotlinx.coroutines.flow.StateFlowimport kotlinx.coroutines.launchclass HomeViewModel(        private val getBeersByPageUsesCase: GetBeersByPageUsesCase,) : ViewModel() {    private val _homeUiState: MutableStateFlow<PagingData<BeerModel>> = MutableStateFlow(value = PagingData.empty())    val homeUiState: StateFlow<PagingData<BeerModel>> = _homeUiState    init {        getBeers()    }    private companion object {        const val NUMBER_PAGE = 20    }    private fun getBeers() {        viewModelScope.launch {            getBeersByPageUsesCase                    .invoke(NUMBER_PAGE)                    .cachedIn(viewModelScope)                    .collect {                        _homeUiState.value = it                    }        }    }}