package com.rendonsoft.beerappdemotest.feature.home.useCasesimport com.rendonsoft.beerappdemotest.feature.home.data.HomeBeerRepositoryimport com.rendonsoft.beerappdemotest.feature.home.domain.model.BeerModelclass GetBeerByIdUsesCase(        private val homeBeerRepository: HomeBeerRepository) {    suspend operator fun invoke(beerId: Int) : BeerModel {        return homeBeerRepository.getBeerById(beerId)    }}