package com.rendonsoft.beerappdemotest.feature.home.useCasesimport androidx.paging.Pagerimport androidx.paging.PagingConfigimport com.rendonsoft.beerappdemotest.feature.home.data.HomeBeerRepositoryclass GetBeersByPageUsesCase(        private val homeBeerRepository: HomeBeerRepository) {    operator fun invoke(pageSize: Int) = Pager(            config = PagingConfig(                    pageSize = pageSize,            ),            pagingSourceFactory = {                homeBeerRepository            }    ).flow}